    set isLocked(val: boolean) {
        this._isLocked = val;
    }

    setDataSource(data: IPepGenericFormDataSource) {
        if (data) {
            this._data = data;
            this.createFormFields();
        }
    }

    setUiControl(view: IPepGenericFormDataView) {
        if (view) {
            this._formData.IsEditable = !this._isLocked;
