                    value = this._data[item.ApiName];
                }
                this._formData.Fields.push(this.createFormField(item, value) as ObjectsDataRowCell);
            });
        }
    }

    getData() {
        return {
            UID: this._formData.UID,
            Values: this._data
        };
    }

    /**
     * updates form field(s) params
