@Injectable()
export class PepDIMXHelperService {
    private dimxFunctions!: {
        DIMXImport: (options?: DIMXImportOptions) => void,
        DIMXRecursiveImport: (options?: DIMXRecursiveImportOptions) => void,
        DIMXExport: (options?: DIMXExportOptions) => void,
        DIMXRecursiveExport: (options?: DIMXRecursiveExportOptions) => void
    };

    constructor(
        private addonBlockLoaderService: PepAddonBlockLoaderService
    ) {
    }

    register(viewContainerRef: ViewContainerRef, dimxHostObject: DIMXHostObject, onDIMXProcessDoneCallback: (dimxEvent: any) => void) {
        return this.addonBlockLoaderService.loadAddonBlockInContainer({
            container: viewContainerRef,
            name: 'DIMX',
            hostObject: dimxHostObject,
            hostEventsCallback: (event: any) => {
                if (event.action === 'DIMXFunctionsRegister') {
                    this.dimxFunctions = event.value;
                } else if (event.action === 'DIMXProcessDone') {
                    onDIMXProcessDoneCallback(event.value);
                } else {
                    console.error('Unknown event action: ' + event.action);
                }
            }
        });
    }

    import(options?: DIMXImportOptions) {
        if (typeof (this.dimxFunctions?.DIMXImport) === 'function') {
            this.dimxFunctions.DIMXImport(options);
        } else {
            console.error('DIMXImport function not found, are you registered?');
        }
    }

    recursive_import(options?: DIMXRecursiveImportOptions) {
        if (typeof (this.dimxFunctions?.DIMXRecursiveImport) === 'function') {
            this.dimxFunctions.DIMXRecursiveImport(options);
        } else {
            console.error('DIMXRecursiveImport function not found, are you registered?');
        }
    }

    export(options?: DIMXExportOptions) {
        if (typeof (this.dimxFunctions?.DIMXExport) === 'function') {
            this.dimxFunctions.DIMXExport(options);
        } else {
            console.error('DIMXExport function not found, are you registered?');
        }
    }

    recursive_export(options?: DIMXRecursiveExportOptions) {
        if (typeof (this.dimxFunctions?.DIMXRecursiveExport) === 'function') {
            this.dimxFunctions.DIMXRecursiveExport(options);
        } else {
            console.error('DIMXRecursiveExport function not found, are you registered?');
        }
    }
}

import { Injectable } from '@angular/core';


@Injectable()
export class PepGenericFormService { 

    constructor() {
        // 
    }
  
    /**
     * checks if the object contains property
     * @param obj object
     * @param prop property name
     * @returns true if contains, false otherwise
    */
    hasProperty(obj: any, prop: string) {
        return Object.prototype.hasOwnProperty.call(obj, prop);
    }

    createFormField(data: any) {
        const field: any = {};
       
        field.ApiName = data.ApiName;
        field.Value = data.controlFieldValue;
        field.FormattedValue =  data.controlFieldValue;
        field.FieldType = data.FieldType;
        field.Enabled = !data.ReadOnly;
        field.Visible = !data.Hidden;
        field.GroupFields = [];
        field.Highlighted = false;
        field.OptionalValues = data.OptionalValues;
        field.AdditionalProps = data.AdditionalProps;    

        return field;
    }

    convertToUiControlField(field: any): any {
        return {
            ApiName: field.ApiName,
            FieldType: field.FieldType,
            Title: field.Title,
            ReadOnly: field.ReadOnlyField,
            Mandatory: field.MandatoryField,
            Hidden: field.Hidden,
            ColumnWidth: field.ColumnWidth,
            ColumnWidthType: 1,
            MaxFieldCharacters: field.MaxCharacters,
            MinValue: field.MinValue,
            MaxValue: field.MaxValue,
            Layout: {
                X: field.Layout.X,
                Y: field.Layout.Y,
                Width: field.Layout.Width,
                Height: field.Layout.Field_Height,
                XAlignment: field.Layout.xAlignment,
                YAlignment: field.Layout.yAlignment,
            }
        }
    }

}

import { Injectable } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import {
    IPepGenericListSmartFilter,
    IPepGenericListDataViewField,
    IPepSmartFilters
} from './generic-list.model';
import {
    PepSmartFilterBaseField,
    IPepSmartFilterData,
} from '@pepperi-addons/ngx-lib/smart-filters';
import { PepQueryBuilderService, IPepQueryBuilderField } from '@pepperi-addons/ngx-lib/query-builder';
import {
    PepRowData,
    ObjectsDataRow
} from '@pepperi-addons/ngx-lib';
import { GridDataViewField, DataViewFieldTypes } from '@pepperi-addons/papi-sdk/dist/entities/data-view';

@Injectable() 
export class PepGenericListService {

    constructor(
        private _translate: TranslateService,
        private _queryBuilderService: PepQueryBuilderService
    ) {
        //
    }

    /**
   * checks if the object contains property
   * @param obj object
   * @param prop property name
   * @returns true if contains, false otherwise
   */
    hasProperty(obj: any, prop: string) {
        return Object.prototype.hasOwnProperty.call(obj, prop);
    }

    getListViewType(dataViewType: string) {
        switch (dataViewType) {
            case 'Grid':
                return 'table';
            case 'Card':
                return 'cards';
            case 'Line':
                return 'lines';
            default:
                return 'table';
        }
    }

    convertToPepRowData(item: any, dataView: any, uuidMapping: string) {
        const row = new PepRowData();
        let itemFields: any;

        if (this.hasProperty(item, 'fields') && typeof item.fields === 'object') {
            itemFields = item.fields;
            if (item.isEditable === false) {
                row.IsEditable = false;
            }
            if (item.isSelectableForActions === false) {
                row.IsSelectableForActions = false;
            }
            if (item.isEditable && item.isSelectableForActions && item.isSelected) {
                row.IsSelected = true;
            }
        } else {
            itemFields = item;
        }

        row.UUID = itemFields[uuidMapping] || undefined;
        row.Fields = [];

        if (dataView?.Fields && dataView.Columns) {
            for (let index = 0; index < dataView.Fields.length; index++) {
                const field = dataView.Fields[index] as IPepGenericListDataViewField;
                row.Fields.push({
                    ApiName: field.FieldID,
                    Title: field.Title ? this._translate.instant(field.Title) : '',
                    XAlignment: 1,
                    FormattedValue: (itemFields[field.FieldID] || '').toString(),
                    Value: (itemFields[field.FieldID] || '').toString(),
                    ColumnWidth: dataView.Columns[index]?.Width || undefined,
                    AdditionalValue: '',
                    OptionalValues: field.OptionalValues || [],
                    FieldType: DataViewFieldTypes[field.Type],
                    ReadOnly: field.ReadOnly,
                    Enabled: !field.ReadOnly
                })
            }
        }
        return row;
    }

    convertToUiControlField(field: any) {
        return {
            ApiName: field.ApiName,
            FieldType: field.FieldType,
            Title: field.Title,
            ReadOnly: field.ReadOnlyField,
            ColumnWidth: field.ColumnWidth,
            ColumnWidthType: 1,
            Layout: {
                X: field.Layout.X,
                Y: field.Layout.Y,
                Width: field.Layout.Width,
                Height: field.Layout.Field_Height,
                XAlignment: field.Layout.xAlignment,
                YAlignment: field.Layout.yAlignment,
            }
        }
    }

    convertToSmartFilter(input: IPepGenericListSmartFilter) {        
        const smartFilters: IPepSmartFilters = {
            fields: [],
            data: [],
            title: 'Filters'
        }

        if (input) {
            if (input.dataView) {
                smartFilters.fields = this.createSmartFilterFields(input.dataView);
                if (input.data?.length) {
                    smartFilters.data = input.data;
                }
            }
            if (input.title) {
                smartFilters.title = input.title;
            }
        }

        return smartFilters;        
    }

    createSmartFilterFields(dataView: any) {
        let smartFields: PepSmartFilterBaseField[] = [];

        const fields = dataView.Fields.map((field: any) => {
            return {
                FieldID: field.FieldID,
                FieldType: field.Type,
                Title: field.Title,
                OptionalValues: field.OptionalValues
            } as IPepQueryBuilderField
        });
        const smartFilterfields = this._queryBuilderService.convertToSmartFilterFields(fields);
        if (smartFilterfields?.length) {
            smartFields = smartFilterfields.map((field => field.smart));
        }

        return smartFields;
    }

    // TODO: This func is copy from ngx-lib write it in one place.
    private getUniqItemId(itemId: string, itemType = ''): string {
        return itemId + ',' + itemType;
    }

    getSelectedItems(items: ObjectsDataRow[]) {
        const selectedItems = [];

        for (let i = 0; i < items.length; i++) {
            if (items[i].IsSelected) {
                selectedItems.push(this.getUniqItemId(items[i].UID, items[i].Type?.toString()));
            }
        }

        return selectedItems;
    }


}

