@import '@pepperi-addons/ngx-lib/src/core/style/abstracts/mixins';
@import '@pepperi-addons/ngx-lib/src/core/style/abstracts/variables';
@import 'ngx-composite-lib/src/core/style/abstracts/variables';

#header {
    height: $header-height;

    .navbar {
        // border: none !important;
        border-radius: 0;
        // @include box-sizing(border-box);
        box-sizing: border-box;
        height: inherit;
        width: 100%;
        padding: $spacing-lg $double-content-padding;
        margin: 0;
        max-width: $max-scr-width;

        @media (max-width: $screen-max-size-xs) {
            padding-left: $content-padding;
            padding-right: $content-padding;
        }

        .navbar-default {
            background-color: transparent;
            border-bottom-color: transparent;
        }
    }
}

#mainCont {
    flex-grow: 1;
    min-height: 100%;
}

#loadingSpinnerModal {
    z-index: $z-index-high + 101;
    height: 1rem;
    width: 100%;
    background-color: transparent;
    opacity: 1;
    position: absolute;
    top: $header-height !important;
    overflow: hidden;
}

#loadingSpinner {
    z-index: $z-index-high;
    width: 100%;
    margin-top: 5px;

    div {
        width: 7px;
        height: 7px;
        position: absolute;
        left: -20px;
        border-radius: 50%;
        animation: move 4s infinite cubic-bezier(0.2, 0.64, 0.81, 0.23);
    }

    div:nth-child(2) {
        animation-delay: 150ms;
    }

    div:nth-child(3) {
        animation-delay: 300ms;
    }

    div:nth-child(4) {
        animation-delay: 450ms;
    }

    div:nth-child(5) {
        animation-delay: 600ms;
    }
}

@keyframes move {
    0% {
        left: 0%;
    }
    50% {
        left: 100%;
    }
    75% {
        left: 100%;
    }
    100% {
        left: 100%;
    }
}

[dir='rtl'] {
    #loadingSpinner {
        div {
            animation: moveOpp 4s infinite cubic-bezier(0.2, 0.64, 0.81, 0.23);
        }

        div:nth-child(2) {
            animation-delay: 150ms;
        }
        div:nth-child(3) {
            animation-delay: 300ms;
        }
        div:nth-child(4) {
            animation-delay: 450ms;
        }
        div:nth-child(5) {
            animation-delay: 600ms;
        }
    }

    @keyframes moveOpp {
        0% {
            left: 100%;
        }
        50% {
            left: -2%;
        }
        75% {
            left: -2%;
        }
        100% {
            left: -2%;
        }
    }
}

.pep-layout {
    display: grid;
    grid-template-rows: $header-height 1fr;
    grid-template-areas: "header"
                         "main";
    height: 100vh;
    width: 100%;
    padding: 0;
    overflow: hidden;

    .pep-header-layout {
        grid-area: header;
        z-index: $z-index-high + 1;
    }

    .pep-main-layout {
        $main-layout-height: calc(100vh - #{$header-height});
        height: 100%; 
        grid-area: main;
        display: flex;
        overflow: hidden;
        
        &.add-padding {
            padding-inline: $content-padding;

            @media (max-width: $screen-max-size-xs) {
                padding-inline: 0;
            }
        }

        .pep-side-layout {
            grid-area: side;
            width: auto;
        }

        .pep-center-layout {
            grid-area: center;
            height: 100%;
            width: 100%;
            overflow: auto;

            ::ng-deep > * {
                height: inherit;
            }
        }
    }
    
    // .pep-footer-layout {
    //     grid-area: footer;
    //     display: block;
    // }
}

@use '@pepperi-addons/ngx-lib/src/core/style/abstracts/mixins';
@use '@pepperi-addons/ngx-lib/src/core/style/abstracts/functions';

#header {
    background-color: functions.get-pep-color(color-system-primary-invert, color-base);

    .navbar {
        box-shadow: functions.get-pep-shadow(shadow-sm, regular);
        
        &.weak {
            @include mixins.state-weak-default(color-top-header);
        }
        
        &.regular {
            @include mixins.state-regular-default(true, color-top-header);
        }
        
        &.strong {
            @include mixins.state-strong-default(color-top-header);
        }
        
        border-right: none !important;
        border-left: none !important;
    }
}

